insert  into `guides_subcategories`(`id_guide_subcategory`,`id_guide_category`,`id_guide_subcategory_type`,`desc_guide_subcategory`,`active_guide_subcategory`,`order_guide_subcategory`) values (11,7,2,'Busca soluciones, no culpables\r\nSé sencillo en el lenguaje, no utilices tecnicismos\r\nExplícale al cliente el motivo de cada uno de los datos que se le solicitan\r\nFacilita los trámites (Ejemplo: Si el cliente no tiene el DNI, dale la opción de facilitar un número de factura o sus apellidos)\r\nIntenta concretar al máximo la información que aporta el cliente para asegurarte de que la respuesta se adecúa a sus necesidades',1,1);
